package ReviewClass04;

public class ForLoopDemo4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=0;//can be anything
		for(System.out.println("1");x<10; System.out.println("3")) {//1 is always first executed, and 3 after the 4, 1 is executed only once
			System.out.println("4");// it is always second executed what is in the body
			x++;
			
		}//starting point, checking condition and then increment point
	}

}
